import React, { Component } from 'react'
import Chart from 'react-apexcharts'
import axios from 'axios'
import Loader from 'react-loader'

export default class body extends Component {
    constructor(props) {
        super(props)

        this.state = {
          loaded:false,
            bubbleseries:[{
              name: "Indian Oil",
              data: []
            }],
            bubbleoptions:{
              chart: {
                height: 350,
                type: 'scatter',
              },
              title: {
                text: 'Indian Oil scatter chart'
              },
              xaxis: {
                type: 'datetime',
            }
          },
            series: [{ 
                data: []
                }],
            options: {
                chart: {
                  type: 'candlestick',
                  height: 700
                },
                title: {
                  text: 'Indian Oil CandleStick Chart',
                  align: 'left'
                },
                xaxis: {
                  type: 'datetime'
                },
                yaxis: {
                  tooltip: {
                    enabled: true
                  }
                }
              },
              lineseries:[{
                name: "Price",
                data:[]
              }],
              lineoption:{
                chart: {
                  type: "area",
                },
                tooltip: {
                  enabled: true,
                  y:{
                    title: {
                      formatter: (Price) => Price
                    }
                  }
                },
                title: {
                  text: ' Indian Oil Line Chart',
                  align: 'left'
                },
                xaxis: {
                  type: 'datetime'
                },
                stroke: {
                  width:3,
                  curve: 'smooth',
                }
              }
            }
        }

    componentDidMount(){
      console.log(this.state);
        axios.get("https://www.moneycontrol.com/mc/widget/basicchart/get_chart_value?classic=true&sc_did=IOC&dur=1yr")
        .then((result)=>{
            // console.log(result.data.g1.length)
            // console.log(new Date(result.data.g1[0].date))
            var series=[];
            var lineseries=[];
            var bubleseries=[];
            // this.setState({lineoption:{xaxis:{min:result.data.g1[0].date}}})
            result.data.g1.map((result,index)=>{
              console.log(result)
              series.push([result.date,[result.open,result.high,result.low,result.close]]);
              lineseries.push([result.date,result.value])
              bubleseries.push(result.date,result.value)
              this.setState({series: [{data:series}],lineseries: [{data:lineseries}], bubbleseries:[{data:lineseries}]});
              // this.setState({lineseries:[{data:[...this.state.lineseries[0].data,[result.date,result.value]]}]})
              // this.setState({series:[{data:[...this.state.series[0].data,[result.date,[result.open,result.high,result.low,result.close]]]}]})
            })
            this.setState({loaded:true})
            console.log(this.state);
        })
    }

    render() {
        return (
          <div className="padding">
            <Loader loaded={this.state.loaded}>
            <Chart options={this.state.bubbleoptions} series={this.state.bubbleseries} type="scatter" width={1000} height={300}/>
            </Loader>
            <Loader loaded={this.state.loaded}>
            <Chart options={this.state.options} series={this.state.series} type="candlestick" width={1000} height={300} />
            </Loader>
            <Loader loaded={this.state.loaded}>
            <Chart options={this.state.lineoption} series={this.state.lineseries} type="line" width={1000} height={300} />
            </Loader>
          </div>
        )
    }
}
