import React, { Component } from 'react'
import axios from 'axios'

export default class searches extends Component {
    constructor(props){
        super(props)
        this.state = {
            id : "",
            value: "",
            open : false,
            searches:[],
            data : []
        }
        this.id=React.createRef();
    }

    redirect = () => {
        this.props.history.push("../")
    }

    dropdown = (e) =>{
        this.handlechange(e);
        axios.get(`http://localhost:5000/getid?id=${e.target.value}`)
        .then((response)=>{
            this.setState({open : true ,searches : response.data.data})
        })
    }

    setid = (data) =>{
        this.id.current.value = data.shareid;
        this.setState({open : !this.state.open , id : data.shareid})
    }

    handlechange = (e) =>{
        this.setState({[e.target.name] : e.target.value})
    }

    search = () => {
        console.log(this.state.id,this.state.value);
    }

    render() {
        return (
            <div>
                <button id="mainpage" onClick={()=>this.redirect()}>Back to Main Page</button>
                <div>
                    <input id="idsearch" placeholder="search by ID" name="id" ref={this.id} onChange={(e)=>this.dropdown(e)}></input>
                    {this.state.open && (<div id="listbox">
                        {this.state.searches.map((data)=>{
                            return(
                                <li id="list" onClick={(e)=>this.setid(data)}>{data.shareid}</li>
                            )
                        })}
                        </div>)
                    }
                    <input id="valuesearch" name="value" placeholder="search by value" onChange={(e)=>this.handlechange(e)}></input>
                    <button className="searchbtn" onClick={()=>this.search()}>search</button>
                </div>
                <div id="table">
                <table>
             <tr>
               <th>date</th>
               <th>open</th>
               <th>high</th>
               <th>low</th>
               <th>close</th>
               <th>volume</th>
               <th>value</th>
             </tr>
             {this.state.data.map((data)=>{
               return(
                 <tr>
                <td>{data.date}</td>
                <td>{data.open}</td>
                <td>{data.high}</td>
                <td>{data.low}</td>
                <td>{data.close}</td>
                <td>{data.volume}</td>
                <td>{data.value}</td>
                 </tr>

               )
             })}
           </table>
                </div>

            </div>
        )
    }
}
