import React, { Component } from 'react'
import axios from 'axios'
import swal from 'sweetalert';

export default class searches extends Component {
    constructor(props){
        super(props)
        this.state = {
            page: 0,
            id : "",
            value: "",
            open : false,
            searches:[],
            data : []
        }
        this.row = React.createRef();
        this.id = React.createRef();
    }

    redirect = () => {
        this.props.history.push("../")
    }

    dropdown = (e) =>{
        this.handlechange(e);
        axios.get(`http://localhost:5000/getid?id=${e.target.value}`)
        .then((response)=>{
            this.setState({open : true ,searches : response.data.data})
        })
    }

    setid = (data) =>{
        this.id.current.value = data.shareid;
        this.setState({open : !this.state.open , id : data.shareid})
    }

    handlechange = (e) =>{
        this.setState({[e.target.name] : e.target.value})
    }

    search = () => {
        if(this.state.id =="" && this.state.value == "")
            swal("Sorry search some id or value",{icon : "warning"})
        else {
            axios.get(`http://localhost:5000/search?id=${this.state.id}&value=${this.state.value}&size=50&from=0`)
            .then((response)=>{
                console.log(response.data.data);
                this.setState({data : response.data.data});
            })
        }
    }

    infinitescroll = () =>{
        let page = this.state.page++;
        axios.get(`http://localhost:5000/search?id=${this.state.id}&value=${this.state.value}&size=50&from=${page}`)
            .then((response)=>{
                let data = response.data.data;
                this.state.data.push(data);
            })
        this.setState({page})
    } 

    listen = () => {
        let elem = this.row;
        let isbottom;
        window.addEventListener('scroll',()=>{
            // console.log(elem);
            isbottom = (elem.current.getBoundingClientRect().bottom+window.innerHeight)
            console.log(isbottom);
            // if(isbottom){
            //     this.infinitescroll();
            // }
        })
    }

    render() {
        return (
            <div >
                <button id="mainpage" onClick={()=>this.redirect()}>Back to Main Page</button>
                <div ref={this.row}>
                    <input autocomplete="off" id="idsearch" placeholder="search by ID" name="id" ref={this.id} onChange={(e)=>this.dropdown(e)}></input>
                    {this.state.open && (<div id="listbox">
                        {this.state.searches.map((data)=>{
                            return(
                                <li id="list" onClick={(e)=>this.setid(data)}>{data.shareid}</li>
                            )
                        })}
                        </div>)
                    }
                    <input autocomplete="off" id="valuesearch" name="value" placeholder="search by value" onChange={(e)=>this.handlechange(e)}></input>
                    <button className="searchbtn" onClick={()=>this.search()}>search</button>
                </div>
                <div id="table" ref = {this.listen}>
                <table>
                    <thead>
             <tr>
               <th>date</th>
               <th>open</th>
               <th>high</th>
               <th>low</th>
               <th>close</th>
               <th>volume</th>
               <th>value</th>
             </tr>
             </thead>
             <tbody>
             {this.state.data.map((data)=>{
               return(
                 <tr>
                <td>{data._source.date}</td>
                <td>{data._source.open}</td>
                <td>{data._source.high}</td>
                <td>{data._source.low}</td>
                <td>{data._source.close}</td>
                <td>{data._source.volume}</td>
                <td>{data._source.value}</td>
                 </tr>

               )
             })}
             </tbody>
           </table>
                </div>

            </div>
        )
    }
}
