import React, { Component } from 'react'
import Chart from 'react-apexcharts'
import axios from 'axios'

export default class body extends Component {
    constructor(props) {
        super(props)

        this.state = {
            dummy:[],
            series: [{
                data: []
                }],
            options: {
                chart: {
                  type: 'candlestick',
                  height: 700
                },
                title: {
                  text: 'CandleStick Chart',
                  align: 'left'
                },
                xaxis: {
                  type: 'datetime'
                },
                yaxis: {
                  tooltip: {
                    enabled: true
                  }
                }
              },
              lineseries:[{
                data:[]
              }],
              lineoption:{
                title: {
                  text: 'Line Chart',
                  align: 'left'
                },
                xaxis: {
                  type: 'datetime'
                },
                stroke: {
                  curve: 'straight',
                }
              }
            }
        }

    componentDidMount(){
        axios.get("https://www.moneycontrol.com/mc/widget/basicchart/get_chart_value?classic=true&sc_did=IOC&dur=1yr")
        .then((result)=>{
            console.log(result);
            // console.log(new Date(result.data.g1[0].date))
            result.data.g1.map((result)=>{
              // console.log(result)
              // this.setState({dummy:[...this.state.dummy,data.date]})
              // this.setState({series:[{data:[this.state.series[0].data,result.date]}]})
              this.setState({lineseries:[{data:[...this.state.series[0].data,[result.value]]}]})
              this.setState({series:[{data:[...this.state.series[0].data,[result.date,[result.open,result.high,result.low,result.close]]]}]})
                // this.setState({series:[{data:[...this.state.series[0].data,{x:new Date(data.date),y:[data.open,data.high,data.low,data.close]}]}]})
            })
            console.log(...this.state.series[0].data);
        })
    }

    render() {
        return (
          <div>
            <Chart options={this.state.options} series={this.state.series} type="candlestick" width={1000} height={320} />
            <Chart options={this.state.lineoption} series={this.state.series} type="candlestick" width={1000} height={320} />
          </div>
        )
    }
}
