import React, { Component } from 'react'
import axios from 'axios'

export default class searches extends Component {
    constructor(props){
        super(props)
        this.state = {
            open : false,
            searches:[],
            data : []
        }
        this.id=React.createRef();
    }

    redirect = () => {
        this.props.history.push("../")
    }

    dropdown = (e) =>{
        axios.get(`http://localhost:5000/getid?id=${e.target.value}`)
        .then((response)=>{
            this.setState({open : !this.state.open ,searches : response.data.data})
        })
    }

    setid = (data) =>{
        this.id.current.value = data.shareid;
        this.setState({open : !this.state.open})
    }

    render() {
        return (
            <div>
                <button id="mainpage" onClick={()=>this.redirect()}>Back to Main Page</button>
                <div>
                    <input id="idsearch" placeholder="search by ID" ref={this.id} onChange={(e)=>this.dropdown(e)}></input>
                    {this.state.open && (<div id="listbox">
                        {this.state.searches.map((data)=>{
                            return(
                                <li id="list" onClick={(e)=>this.setid(data)}>{data.shareid}</li>
                            )
                        })}
                        </div>)
                    }
                    <button className="searchbtn">search by ID</button>
                    <p1>Or</p1>
                    <input id="valuesearch" placeholder="search by value"></input>
                    <button className="searchbtn">search by value</button>
                </div>
                <div id="table">
                <table>
             <tr>
               <th>date</th>
               <th>open</th>
               <th>high</th>
               <th>low</th>
               <th>close</th>
               <th>volume</th>
               <th>value</th>
             </tr>
             {this.state.data.map((data)=>{
               return(
                 <tr>
                <td>{data.date}</td>
                <td>{data.open}</td>
                <td>{data.high}</td>
                <td>{data.low}</td>
                <td>{data.close}</td>
                <td>{data.volume}</td>
                <td>{data.value}</td>
                 </tr>

               )
             })}
           </table>
                </div>

            </div>
        )
    }
}
