import React, { Component } from 'react'
import axios from 'axios';
import {TextField,Button} from '@material-ui/core'
import Chart from 'react-apexcharts'

export default class logs extends Component {

    constructor(props){
        super(props);
        this.state = {
            data : [],
            limit: 20,
            option1:{
                title:{
                    text: "Status Chart",
                    align : "centre"
                },
                labels: [],
            },
            series1:[],
            option2:{
                labels: [],
            },
            series2:[]
        }
    }

    componentDidMount = () => {
        axios.get(`http://localhost:5000/logs?limit=${this.state.limit}`)
        .then((response)=>{
            this.setState({data : response.data.data})
        })
        axios.get(`http://localhost:5000/statuscount`)
        .then((response)=>{
            console.log(response.data.data)
            response.data.data.code_count.buckets.map((data)=>{
                this.state.option1.labels.push(data.key)
                this.state.series1.push(data.doc_count)
            })
        })
    }

    redirect = () =>{
        this.props.history.push("../")
    }

    setlimit = () =>{
        axios.get(`http://localhost:5000/logs?limit=${this.state.limit}`)
        .then((response)=>{
            this.setState({data : response.data.data})
        })
    }

    limit = (e) => { 
            this.setState({limit:e.target.value})
    }

    render() {
        console.log(this.state.limit)
        return (
            <div className="logs">
                <label>LIMIT :</label><TextField type="Number" onChange={(e)=>this.limit(e)} value={this.state.limit} inputProps={{ min:"10",max:"100" ,step:"10"}}/>
                <Button variant="contained" onClick={()=>this.setlimit()} color="primary">SET</Button>
                <button id="searchpage" onClick={()=>this.redirect()}>Main Page</button>
                <div id="logtable">
                    <table>
                        <tr id="colorhead">
                            <th>CLEINT</th>
                            <th>METHOD</th>
                            <th>STATUS</th>
                            <th>AGENT</th>
                        </tr>
                        {this.state.data.map((data)=>{
                            return(
                            <tr>
                                <td>{data._source.client}</td>
                                <td>{data._source.method}</td>
                                <td>{data._source.code}</td>
                                <td>{data._source.agent}</td>
                            </tr>)
                        })}
                    </table>
                </div>
                <div id="piecharts">
                <Chart className="pie" options={this.state.option1} series={this.state.series1} type="pie" height={350} width={350}/>
                <Chart className="pie2" options={this.state.option2} series={this.state.series2} type="pie" height={350} width={350}/>
                </div>
            </div>
        )
    }
}
